import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';

const useStyles = makeStyles({
  root: {
    maxWidth: 345,
  },
});

var petalas = 0

export function Cartao() {
  const classes = useStyles();

  return (
    <Card className={classes.root}>
      <CardActionArea>
        <CardMedia
          component="img"
          alt="Contemplative Tree"
          height="140"
          width="340"
          image="http://1.bp.blogspot.com/--RtSvRLAzTU/U4nWcApwPgI/AAAAAAAACXM/7rG0hTpCoIc/s1600/9456-fantasy-landscape-wallpaper-full-hd-38690.jpg"
          title="A Cerejeira Mágica"
        />
        <CardContent>
          <Typography gutterBottom variant="h5" component="h2">
            Cerejeira
          </Typography>
          <Typography variant="body2" color="textSecondary" component="p">
           Conte as pétalas que passam ao seu redor e veja se é seu dia de sorte
            
          </Typography>
        </CardContent>
      </CardActionArea>
      <CardActions>
        <Button size="small" color="primary" variant="contained" onClick={() => adciona(petalas)}>
          Adcionar
        </Button>
        <Button size="small" color="secondary" variant="contained">
          Remover
        </Button>
        <Typography variant="h2">{petalas}</Typography>
      </CardActions>
    </Card>
  );
}


 function adciona(param){
    
    console.log(petalas)
    return petalas++;
}
