import styled from "styled-components";

export default Card



export const CardMain = styled.div`
display: flex;
justify-content: center;
padding-top: 25px;
width: 80px;
height: 120px;
background-color: #dfdfdf;
margin: 0 auto;
border: 2px solid black;
border-radius: 5%;
font-size: ${(props) => props.fontSize};

`
